import org.scalatest.wordspec.AnyWordSpec
import org.scalatest.matchers.should.Matchers._
import de.htwg.se.monopoly._
class MonopolySpec extends AnyWordSpec {
  "Monopoly" should {
    "have a bar as String of form '┌──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┐'" in {
      barTop() should be("┌──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┬──────────┐" + eol)
    }
    "have a scalable bar" in {
      barTop(1,1) should be("┌─┐" + eol)
      barTop(1,2) should be("┌──┐" + eol)
      barTop(2,1) should be("┌─┬─┐" + eol)
    }
    "have cells as String of form '│          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │'" in {
      cells() should be("""│          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │
                          |│          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │
                          |│          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │
                          |│          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │
                          |│          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │
                          |│          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │
                          |│          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │
                          |│          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │
                          |│          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │          │""".stripMargin + eol)
    }
    "have scalable cells" in {
      cells(1, 1, 1) should be("│ │" + eol)
      cells(1, 2, 1) shold be ("│  │" + eol) 
      cells(1, 1, 2) shold be ("""│ │
                                 |│ │""".stripMargin + eol)
      cells(1, 2, 2) shold be ("""│  │
                                 |│  │""".stripMargin + eol)
      cells(2, 1, 1) shold be ("│ │ │" + eol)
      cells(2, 2, 1) shold be ("│  │  │" + eol)
      cells(2, 1, 2) shold be ("""│ │ │
                                 |│ │ │""".stripMargin + eol)
      cells(2, 2, 2) shold be ("""│  │  │
                                 |│  │  │""".stripMargin + eol)
    }
    "have a bar as String of form '└──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┘'" in {
      barBottom() should be("└──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┴──────────┘" + eol)
    }
    "have a scalable bar" in {
      barBottom(1, 1) should be("└─┘" + eol)
      barBottom(1, 2) should be("└──┘" + eol)
      barBottom(2, 1) should be("└─┴─┘" + eol)
    }
  }
}
